// src/components/AccountConfirmation.js

import React from 'react';
import { Link } from 'react-router-dom';

const AccountConfirmation = () => {
    return (
        <div className="container mt-5">
            <div className="row">
                <div className="col-md-6 mx-auto">
                    <div className="card" style={{ border: 'none' }}>
                        <div className="card-header text-center card-header-custom">
                            <h2>Account Confirmation</h2>
                        </div>
                        <div className="card-body text-center">
                            <p>Thank you for registering!</p>
                            <p>Please check your email to confirm your registration.</p>
                            <p>If you did not receive the email, you can request a new confirmation link.</p>
                            <Link to="/resend-confirmation" className="btn btn-primary">
                                Resend Confirmation Email
                            </Link>
                            <hr />
                            <p>
                                Already have an account?{' '}
                                <Link to="/login" className="text-dark">Sign in</Link>
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AccountConfirmation;
