// src/components/Birthdays.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Birthdays = () => {
    const [birthdays, setBirthdays] = useState([]);
    const [name, setName] = useState('');
    const [date, setDate] = useState('');
    const [editId, setEditId] = useState(null); // To handle editing
    const token = localStorage.getItem('token');

    useEffect(() => {
        const fetchBirthdays = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/api/birthdays/', {
                    headers: { Authorization: `Token ${token}` }
                });
                setBirthdays(response.data);
            } catch (error) {
                console.error('Error fetching birthdays:', error.response.data);
            }
        };

        fetchBirthdays();
    }, [token]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (editId) {
                // Update existing birthday
                await axios.put(`http://127.0.0.1:8000/api/birthdays/${editId}/`, {
                    name,
                    date,
                }, {
                    headers: { Authorization: `Token ${token}` }
                });
                setEditId(null); // Reset edit ID after saving
            } else {
                // Create new birthday
                await axios.post('http://127.0.0.1:8000/api/birthdays/', {
                    name,
                    date,
                }, {
                    headers: { Authorization: `Token ${token}` }
                });
            }
            setName('');
            setDate('');
            // Re-fetch birthdays
            fetchBirthdays();
        } catch (error) {
            console.error('Error saving birthday:', error.response.data);
        }
    };

    const handleEdit = (birthday) => {
        setName(birthday.name);
        setDate(birthday.date);
        setEditId(birthday.id); // Set ID to edit
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://127.0.0.1:8000/api/birthdays/${id}/`, {
                headers: { Authorization: `Token ${token}` }
            });
            // Re-fetch birthdays
            fetchBirthdays();
        } catch (error) {
            console.error('Error deleting birthday:', error.response.data);
        }
    };

    return (
        <div>
            <h2>Birthdays</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                />
                <input
                    type="date"
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                    required
                />
                <button type="submit">{editId ? 'Update Birthday' : 'Add Birthday'}</button>
            </form>
            <ul>
                {birthdays.map((birthday) => (
                    <li key={birthday.id}>
                        {birthday.name} - {birthday.date}
                        <button onClick={() => handleEdit(birthday)}>Edit</button>
                        <button onClick={() => handleDelete(birthday.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Birthdays;
