import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; 
import { fetchBirthdays, createBirthday, updateBirthday, deleteBirthday, fetchUserProfile } from '../services/api'; 

const Birthdays = () => {
    const [birthdays, setBirthdays] = useState([]);
    const [newBirthday, setNewBirthday] = useState({ name: '', birthdate: '' }); // State for new birthday
    const [editBirthday, setEditBirthday] = useState(null); // State for the birthday being edited
    const [deleteId, setDeleteId] = useState(null); // ID for the birthday being deleted
    const [error, setError] = useState(null); 
    const [userProfile, setUserProfile] = useState(null); 
    const [showEditModal, setShowEditModal] = useState(false); // Modal for editing
    const [showDeleteModal, setShowDeleteModal] = useState(false); // Modal for deleting
    const token = localStorage.getItem('authToken'); 
    const navigate = useNavigate(); 

    useEffect(() => {
        const loadProfileAndBirthdays = async () => {
            try {
                const profileData = await fetchUserProfile(token);
                setUserProfile(profileData); 

                const birthdayData = await fetchBirthdays(token);
                setBirthdays(birthdayData); 
            } catch (err) {
                setError(err.detail || 'Error fetching data');
                console.error('Error fetching data:', err);
            }
        };

        loadProfileAndBirthdays(); 
    }, [token]); 

    const handleCreate = async (e) => {
        e.preventDefault();
        setError(null); 

        const birthdayData = {
            user: userProfile ? userProfile.id : null, 
            description: newBirthday.name, 
            date: newBirthday.birthdate 
        };

        try {
            await createBirthday(birthdayData, token);
            setNewBirthday({ name: '', birthdate: '' }); 
            const data = await fetchBirthdays(token);
            setBirthdays(data);
        } catch (error) {
            const errorMessage = error.user ? error.user[0] : 'Error saving birthday: ' + (error.detail || 'An error occurred.');
            setError(errorMessage);
            console.error('Error saving birthday:', error);
        }
    };

    const handleEdit = (birthday) => {
        setEditBirthday(birthday);
        setShowEditModal(true);
    };

    const handleDelete = async (id) => {
        try {
            await deleteBirthday(id, token);
            const data = await fetchBirthdays(token);
            setBirthdays(data);
        } catch (error) {
            setError('Error deleting birthday: ' + (error.detail || 'An error occurred.'));
            console.error('Error deleting birthday:', error);
        }
    };

    const handleLogout = () => {
        navigate('/logout');
    };

    const handleUpdate = async () => {
        if (!editBirthday) return;

        try {
            await updateBirthday(editBirthday.id, { description: editBirthday.description, date: editBirthday.date }, token);
            setShowEditModal(false);
            const data = await fetchBirthdays(token);
            setBirthdays(data);
            setEditBirthday(null);
        } catch (error) {
            setError('Error updating birthday: ' + (error.detail || 'An error occurred.'));
            console.error('Error updating birthday:', error);
        }
    };

    return (
        <div>
            <div className="container d-flex justify-content-center align-items-center my-5">
                <div className="col-md-4">
                    <h2 className="text-center mb-4">Add a Birthday</h2>
                    <form onSubmit={handleCreate}>
                        <input
                            type="text"
                            placeholder="Name"
                            value={newBirthday.name}
                            onChange={(e) => setNewBirthday({ ...newBirthday, name: e.target.value })}
                            className="form-control mb-2"
                            required
                        />
                        <input
                            type="date"
                            value={newBirthday.birthdate}
                            onChange={(e) => setNewBirthday({ ...newBirthday, birthdate: e.target.value })}
                            className="form-control mb-2"
                            required
                        />
                        <button type="submit" className="btn btn-primary w-100">Add Birthday</button>
                    </form>
                </div>
            </div>

            {/* Empty State */}
            {birthdays.length === 0 ? (
                <div className="text-center">
                    <p><strong>No birthdays added yet.</strong></p>
                    <p>Click the button above to add your first birthday!</p>
                    <img
                        src="https://res.cloudinary.com/dnbbm9vzi/image/upload/v1729520568/Group_27_yp99rq.png"
                        alt="No Birthdays"
                        className="img-fluid mb-3"
                        style={{ width: '450px' }} 
                    />
                </div>
            ) : (
                <div className="row">
                    {birthdays.map((birthday) => (
                        <div className="col-md-4 mb-3" key={birthday.id}>
                            <div className="card" style={{ backgroundColor: '#F8F7F5' }}>
                                <div className="card-body text-center">
                                    <h5 className="card-title">{birthday.description}</h5>
                                    <p className="card-text">{birthday.date}</p>
                                    <div className="d-flex justify-content-between">
                                        <button onClick={() => handleEdit(birthday)} className="btn btn-warning">Edit</button>
                                        <button onClick={() => { setDeleteId(birthday.id); setShowDeleteModal(true); }} className="btn btn-danger">Delete</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            )}

            {/* Edit Birthday Modal */}
            {showEditModal && (
                <div className="modal fade show" style={{ display: 'block' }} aria-modal="true" role="dialog">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Edit Birthday</h5>
                                <button type="button" className="btn-close" onClick={() => setShowEditModal(false)} aria-label="Close"></button>
                            </div>
                            <div className="modal-body">
                                {editBirthday && (
                                    <>
                                        <input
                                            type="text"
                                            value={editBirthday.description}
                                            onChange={(e) => setEditBirthday({ ...editBirthday, description: e.target.value })}
                                            className="form-control mb-2"
                                            required
                                        />
                                        <input
                                            type="date"
                                            value={editBirthday.date}
                                            onChange={(e) => setEditBirthday({ ...editBirthday, date: e.target.value })}
                                            className="form-control mb-2"
                                            required
                                        />
                                    </>
                                )}
                            </div>
                            <div className="modal-footer d-flex justify-content-between">
                                <button type="button" className="btn btn-secondary" onClick={() => setShowEditModal(false)}>Close</button>
                                <button type="button" className="btn btn-success" onClick={handleUpdate}>Update Birthday</button>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Confirm Deletion Modal */}
            {showDeleteModal && (
                <div className="modal fade show" style={{ display: 'block' }} aria-modal="true" role="dialog">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Confirm Deletion</h5>
                                <button type="button" className="btn-close" onClick={() => setShowDeleteModal(false)} aria-label="Close"></button>
                            </div>
                            <div className="modal-body">
                                <p>Are you sure you want to delete this birthday?</p>
                            </div>
                            <div className="modal-footer d-flex justify-content-between">
                                <button type="button" className="btn btn-secondary" onClick={() => setShowDeleteModal(false)}>Cancel</button>
                                <button type="button" className="btn btn-danger" onClick={() => handleDelete(deleteId)}>Delete</button>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {error && <div style={{ color: 'red', textAlign: 'center' }}>{error}</div>} {/* Show error messages */}
        </div>
    );
};

export default Birthdays;
