import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Import useNavigate for navigation
import { fetchBirthdays, createBirthday, updateBirthday, deleteBirthday, fetchUserProfile } from '../services/api'; // Import from api.js

const Birthdays = () => {
    const [birthdays, setBirthdays] = useState([]);
    const [newBirthday, setNewBirthday] = useState({ name: '', birthdate: '' });
    const [editBirthday, setEditBirthday] = useState(null);
    const [showEditModal, setShowEditModal] = useState(false);
    const [showDeleteModal, setShowDeleteModal] = useState(false);
    const [deleteId, setDeleteId] = useState(null);
    const [error, setError] = useState(null); // To store any error messages
    const [userProfile, setUserProfile] = useState(null); // State to store user profile
    const token = localStorage.getItem('authToken'); // Retrieve token from local storage
    const navigate = useNavigate(); // Initialize navigate function

    // Load user profile and birthdays when the component mounts
    useEffect(() => {
        const loadProfileAndBirthdays = async () => {
            try {
                // Fetch user profile
                const profileData = await fetchUserProfile(token);
                setUserProfile(profileData); // Set user profile

                // Fetch birthdays
                const birthdayData = await fetchBirthdays(token);
                setBirthdays(birthdayData); // Set birthdays
            } catch (err) {
                setError(err.detail || 'Error fetching data');
                console.error('Error fetching data:', err);
            }
        };

        loadProfileAndBirthdays(); // Call the fetch function when component mounts
    }, [token]); // Dependencies: token

    const handleCreate = async () => {
        setError(null); // Reset error on submit

        // Prepare birthday data
        const birthdayData = {
            user: userProfile ? userProfile.id : null, // Convert userId to an integer
            description: newBirthday.name, // Use `description` for name
            date: newBirthday.birthdate // Ensure this date is in the correct format (YYYY-MM-DD)
        };

        try {
            // Create new birthday
            await createBirthday(birthdayData, token);
            setNewBirthday({ name: '', birthdate: '' });
            // Re-fetch birthdays after creating
            const data = await fetchBirthdays(token);
            setBirthdays(data);
        } catch (error) {
            // Handle error and show appropriate message
            const errorMessage = error.user ? error.user[0] : 'Error saving birthday: ' + (error.detail || 'An error occurred.');
            setError(errorMessage);
            console.error('Error saving birthday:', error);
        }
    };



    const handleDelete = async (id) => {
        try {
            await deleteBirthday(id, token);
            // Re-fetch birthdays after deletion
            const data = await fetchBirthdays(token);
            setBirthdays(data);
        } catch (error) {
            setError('Error deleting birthday: ' + (error.detail || 'An error occurred.'));
            console.error('Error deleting birthday:', error);
        }
    };

    const handleLogout = () => {
        // Redirect to the Logout component
        navigate('/logout');
    };

    return (
        <div className='dashboard-background-bday'>
            <div className={`container-fluid`}>
                {/* Show error message if it exists */}
                {error && <div style={{ color: 'red' }}>{error}</div>}
                <div className="row">
                    {/* Sidebar */}
                    <nav className="col-md-2 d-none d-md-block sidebar">
                        <h2 className="sidebar-heading text-center">Menu</h2>
                        <ul className="nav flex-column">
                            <li className="nav-item">
                                <Link className="nav-link" to="/dashboard">Dashboard</Link>
                            </li>
                            <hr className="divider" />
                            <li className="nav-item">
                                <Link className="nav-link" to="/birthdays">Birthdays</Link>
                            </li>
                            <hr className="divider" />
                        </ul>

                        {/* User Info and Logout Section */}
                        <div className="sidebar-user-info mt-4 text-center">
                            {/* Replace userProfile with your actual user data */}
                            {userProfile && (
                                <p>Welcome, {userProfile.username}! (ID: {userProfile.id})</p>
                            )}
                            <button 
                                onClick={handleLogout} 
                                className="btn btn-sm btn-danger"
                            >
                                Logout
                            </button>
                        </div>
                    </nav>

                    {/* Main Content */}
                    <main className="col-md-9 ml-sm-auto col-lg-10 px-4">
                        <h2 className="text-center my-4">Birthdays</h2>
                        {error && <div style={{ color: 'red' }}>{error}</div>} {/* Show error messages */}

                        {/* Add Birthday Form */}
                        <div className="d-flex justify-content-center align-items-center my-5">
                            <div className="col-md-6">
                                <h4 className="text-center mb-4">Add a Birthday</h4>
                                <input
                                    type="text"
                                    placeholder="Name"
                                    value={newBirthday.name}
                                    onChange={(e) => setNewBirthday({ ...newBirthday, name: e.target.value })}
                                    className="form-control mb-2"
                                />
                                <input
                                    type="date"
                                    value={newBirthday.birthdate}
                                    onChange={(e) => setNewBirthday({ ...newBirthday, birthdate: e.target.value })}
                                    className="form-control mb-2"
                                />
                                <button onClick={handleCreate} className="btn btn-primary w-100">Add Birthday</button>
                            </div>
                        </div>

                        {/* Empty State */}
                        {birthdays.length === 0 ? (
                            <div className="text-center">
                                <p><strong>No birthdays added yet.</strong></p>
                                <p>Click the button above to add your first birthday!</p>
                                <img
                                    src="https://res.cloudinary.com/dnbbm9vzi/image/upload/v1729520568/Group_27_yp99rq.png"
                                    alt="No Birthdays"
                                    className="img-fluid mb-3"
                                    style={{ width: '450px' }} // Optional: Adjust the size of the image
                                />
                            </div>
                        ) : (
                            <div className="row">
                                {birthdays.map((birthday) => (
                                    <div className="col-md-4 mb-3" key={birthday.id}>
                                        <div className="card" style={{ backgroundColor: '#F8F7F5' }}>
                                            <div className="card-body text-center">
                                                <h5 className="card-title">{birthday.description}</h5>
                                                <p className="card-text">{birthday.date}</p>
                                                <div className="d-flex justify-content-between">
                                                    <button onClick={() => handleEdit(birthday)} className="btn btn-warning">Edit</button>
                                                    <button onClick={() => { setDeleteId(birthday.id); setShowDeleteModal(true); }} className="btn btn-danger">Delete</button>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}

                        {/* Edit Birthday Modal */}
                        {showEditModal && (
                            <div className="modal fade show" style={{ display: 'block' }} aria-modal="true" role="dialog">
                                <div className="modal-dialog">
                                    <div className="modal-content">
                                        <div className="modal-header">
                                            <h5 className="modal-title">Edit Birthday</h5>
                                            <button type="button" className="btn-close" onClick={() => setShowEditModal(false)} aria-label="Close"></button>
                                        </div>
                                        <div className="modal-body">
                                            {editBirthday && (
                                                <>
                                                    <input
                                                        type="text"
                                                        value={editBirthday.description}
                                                        onChange={(e) => setEditBirthday({ ...editBirthday, description: e.target.value })}
                                                        className="form-control mb-2"
                                                    />
                                                    <input
                                                        type="date"
                                                        value={editBirthday.date}
                                                        onChange={(e) => setEditBirthday({ ...editBirthday, date: e.target.value })}
                                                        className="form-control mb-2"
                                                    />
                                                </>
                                            )}
                                        </div>
                                        <div className="modal-footer">
                                            <button type="button" className="btn btn-secondary" onClick={() => setShowEditModal(false)}>Close</button>
                                            <button type="button" className="btn btn-primary" onClick={handleUpdate}>Save changes</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Delete Confirmation Modal */}
                        {showDeleteModal && (
                            <div className="modal fade show" style={{ display: 'block' }} aria-modal="true" role="dialog">
                                <div className="modal-dialog">
                                    <div className="modal-content">
                                        <div className="modal-header">
                                            <h5 className="modal-title">Delete Birthday</h5>
                                            <button type="button" className="btn-close" onClick={() => setShowDeleteModal(false)} aria-label="Close"></button>
                                        </div>
                                        <div className="modal-body">
                                            <p>Are you sure you want to delete this birthday?</p>
                                        </div>
                                        <div className="modal-footer">
                                            <button type="button" className="btn btn-secondary" onClick={() => setShowDeleteModal(false)}>Cancel</button>
                                            <button type="button" className="btn btn-danger" onClick={() => { handleDelete(deleteId); setShowDeleteModal(false); }}>Delete</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}
                    </main>
                </div>
            </div>
        </div>
    );
};

export default Birthdays;
