// src/components/Birthdays.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Birthdays = () => {
    const [birthdays, setBirthdays] = useState([]);
    const [name, setName] = useState('');
    const [date, setDate] = useState('');
    const token = localStorage.getItem('token'); // Retrieve the token from local storage

    useEffect(() => {
        const fetchBirthdays = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/api/birthdays/', {
                    headers: { Authorization: `Token ${token}` } // Add token to headers
                });
                setBirthdays(response.data);
            } catch (error) {
                console.error('Error fetching birthdays:', error.response.data);
                // Handle the error appropriately (e.g., show a message to the user)
            }
        };

        fetchBirthdays();
    }, [token]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://127.0.0.1:8000/api/birthdays/', {
                name,
                date,
            }, {
                headers: { Authorization: `Token ${token}` } // Add token to headers
            });
            setName('');
            setDate('');
            // Re-fetch birthdays after adding
            const response = await axios.get('http://127.0.0.1:8000/api/birthdays/', {
                headers: { Authorization: `Token ${token}` } // Add token to headers
            });
            setBirthdays(response.data);
        } catch (error) {
            console.error('Error adding birthday:', error.response.data);
            // Handle the error appropriately (e.g., show a message to the user)
        }
    };

    return (
        <div>
            <h2>Birthdays</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                />
                <input
                    type="date"
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                    required
                />
                <button type="submit">Add Birthday</button>
            </form>
            <ul>
                {birthdays.map((birthday) => (
                    <li key={birthday.id}>
                        {birthday.name} - {birthday.date}
                        {/* Add buttons for update and delete */}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Birthdays;
