import React, { useEffect, useState } from 'react';
import {
    fetchHolidays,
    createHoliday,
    updateHoliday,
    deleteHoliday,
} from './api'; // Make sure to adjust the import path for your API functions

const Holidays = ({ token }) => {
    const [holidays, setHolidays] = useState([]);
    const [description, setDescription] = useState('');
    const [month, setMonth] = useState('');
    const [day, setDay] = useState('');
    const [currentId, setCurrentId] = useState(null);
    const [editing, setEditing] = useState(false);

    useEffect(() => {
        loadHolidays();
    }, []);

    const loadHolidays = async () => {
        try {
            const data = await fetchHolidays(token);
            setHolidays(data);
        } catch (error) {
            console.error("Error fetching holidays:", error);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const holidayData = { month: parseInt(month), day: parseInt(day), description };

        try {
            if (editing) {
                await updateHoliday(currentId, holidayData, token);
            } else {
                await createHoliday(holidayData, token);
            }
            resetForm();
            loadHolidays();
        } catch (error) {
            console.error("Error saving holiday:", error);
        }
    };

    const resetForm = () => {
        setDescription('');
        setMonth('');
        setDay('');
        setCurrentId(null);
        setEditing(false);
    };

    const handleEdit = (holiday) => {
        setDescription(holiday.description);
        setMonth(holiday.month);
        setDay(holiday.day);
        setCurrentId(holiday.id);
        setEditing(true);
    };

    const handleDelete = async (id) => {
        if (window.confirm("Are you sure you want to delete this holiday?")) {
            try {
                await deleteHoliday(id, token);
                loadHolidays();
            } catch (error) {
                console.error("Error deleting holiday:", error);
            }
        }
    };

    return (
        <div>
            <h1>Holidays</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>
                        Month (1-12):
                        <input 
                            type="number" 
                            value={month} 
                            onChange={(e) => setMonth(e.target.value)} 
                            min="1" 
                            max="12" 
                            required 
                        />
                    </label>
                </div>
                <div>
                    <label>
                        Day (1-31):
                        <input 
                            type="number" 
                            value={day} 
                            onChange={(e) => setDay(e.target.value)} 
                            min="1" 
                            max="31" 
                            required 
                        />
                    </label>
                </div>
                <div>
                    <label>
                        Description:
                        <textarea 
                            value={description} 
                            onChange={(e) => setDescription(e.target.value)} 
                        />
                    </label>
                </div>
                <button type="submit">{editing ? 'Update Holiday' : 'Add Holiday'}</button>
                <button type="button" onClick={resetForm}>Cancel</button>
            </form>

            <h2>Holiday List</h2>
            <ul>
                {holidays.map(holiday => (
                    <li key={holiday.id}>
                        <strong>{`${holiday.month}/${holiday.day}`}</strong>: {holiday.description}
                        <button onClick={() => handleEdit(holiday)}>Edit</button>
                        <button onClick={() => handleDelete(holiday.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Holidays;
