// src/services/api.js
import axios from 'axios';

const API_URL = 'http://127.0.0.1:8000'; // Your Django backend URL

// Function to register a new user
export const registerUser = async (userData) => {
    try {
        const response = await axios.post(`${API_URL}/auth/register/`, userData);
        return response.data;
    } catch (error) {
        throw error.response.data; // You can customize this based on your needs
    }
};

// Function to log in a user
export const loginUser = async (credentials) => {
    try {
        const response = await axios.post(`${API_URL}/auth/login/`, credentials);
        return response.data;
    } catch (error) {
        throw error.response.data; // Handle the error response
    }
};

// Function to fetch birthdays
export const fetchBirthdays = async (token) => {
    try {
        const response = await axios.get(`${API_URL}/api/birthdays/`, {
            headers: { Authorization: `Token ${token}` }
        });
        return response.data;
    } catch (error) {
        throw error.response.data; // Handle the error response
    }
};

// Function to create a new birthday
export const createBirthday = async (birthdayData, token) => {
    try {
        const response = await axios.post(`${API_URL}/api/birthdays/`, birthdayData, {
            headers: { Authorization: `Token ${token}` }
        });
        return response.data;
    } catch (error) {
        throw error.response.data; // Handle the error response
    }
};

// Add more functions as needed (e.g., updateBirthday, deleteBirthday)
